// A function to get (extract) the token from the browser's localStorage
export function getAuthToken() {

  // Look inside localStorage for an item saved with the key "token"
  // localStorage is like a small storage space inside the browser
  // Example: if we saved localStorage.setItem("token", "abc123"), 
  // this will retrieve "abc123"
  const token = localStorage.getItem("token");

  // 2. Return the token so other parts of the app can use it
  // If there is no token saved, this will return null
  return token;
}



export function tokenLoader() {
    return getAuthToken()
}
